import styled from 'styled-components';
import { useEffect, useState } from 'react';

import Input from "../Input";
import { get_all_itens } from "../../services/home";
// import { getType } from "../../services/type";
import { post_fav } from "../../services/fav";

// const imagens_pokemons = require.context('../../img/img_pokemons', false, /\.(png|jpe?g|svg)$/);
// const getType = require.context('../../img/img_types', false, /\.(png|jpe?g|svg)$/);

function getImagemPokemon(id) {
    try {
        return get_all_itens(`./${id}.png`);
    } catch {
        return null;
    }
}

const PesquisaContainer = styled.section`
background-image: linear-gradient(90deg, #002F52 35%, #326589 165%);
color: #FFF;
text-align: center;
padding: 40px 0;
width: 80%;
margin: auto;
box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.25);
// box-shadow: 0px 4px 0px rgba(0, 0, 0, 0.8);
transition: all 0.3s ease;

`;

const Titulo = styled.h2`
    color: #FFF;
    font-size: 36px;
    text-align: center;
    width: 100%;
`;

// const Subtitulo = styled.h3`
//     font-size: 16px;
//     font-weight: 500;
//     margin-bottom: 40px;
// `;

const ResultadosGrid = styled.div`
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(150px, 1fr)); /* até 5 colunas */
    
    max-width: 1000px;
    margin: 20px auto;
    gap: 20px;
`;

const Resultado = styled.div`
    background: rgba(255, 255, 255, 0.1);
    padding: 10px;
    border-radius: 12px;
    text-align: center;
    cursor: pointer;
    transition: 0.2s;

    
    p {
        margin-top: 8px;
        font-size: 14px;
        color: #fff;
    }

    img {
        width: 100px;
        height: 100px;
        object-fit: contain;
    }
    
    &:hover {
        border: 1px solid white;
        transform: scale(1.05);
    }
`;

function Pesquisa() {
    const [researched_item, set_researched_item] = useState([]);
    const [itens, set_itens] = useState([]);

    // useEffect(() => {
    //     fetch_itens();
    // }, []);

    // async function fetch_itens() {
    //     const reacdex_api = await get_all_itens();
    //     set_itens(reacdex_api);
    // }
    useEffect(() => {
        async function fetchPokemons() {
            try {
                const response = await fetch("http://localhost:8000/home");
                if (!response.ok) throw new Error("Erro ao buscar pokemons");
                const data = await response.json();
                setPokemons(data);
            } catch (err) {
                console.error("Falha no fetch:", err.message);
                setPokemons([]);
            } finally {
                setLoading(false);
            }
        }
        fetchPokemons();
    }, []);

    async function insert_fav(id) {
        await post_fav(id);
        alert(`Item de id:${id} inserido!`);
    }

    return (
        <PesquisaContainer vazio={researched_item.length === 0}>
            <Titulo>Pesquise por um pokemon</Titulo>
            <Input
                placeholder="Pesquisar por nome"
                onBlur={evento => {
                    const textoDigitado = evento.target.value;
                    const resultadoPesquisa = itens.filter(item =>
                        item.name?.toLowerCase().includes(textoDigitado.toLowerCase())
                    );
                    set_researched_item(resultadoPesquisa);
                }}
            />

            {researched_item.length > 0 && (
                <ResultadosGrid key={pokemon.id}>>
                    {researched_item.map(item => (
                        <Resultado key={item.id} onClick={() => insert_fav(item.id)}>
                            <PokemonImage
                                src={pokemon.image || `http://localhost:8000/home/${pokemon.id}/img`}
                                alt={pokemon.name}
                            />
                            <p>{item.name}</p>
                        </Resultado>
                    ))}
                </ResultadosGrid>
            )}
        </PesquisaContainer>
    );
}

export default Pesquisa;
