import styled from "styled-components";

const InputCxBx = styled.input.attrs({ type: "checkbox" })`
  appearance: none; /* remove estilo nativo */
  -webkit-appearance: none; /* compatibilidade Safari */
  -moz-appearance: none; /* compatibilidade Firefox */
  
  cursor: pointer;
  width: 20px;
  height: 20px;

  border: 2px solid #fff;
  border-radius: 50%; /* mantém circular */
  display: inline-block;
  background: #ffffff4c;

  transition: 
  background-color 0.5s ease, 
  border 1s ease, 
  transform 0.3s ease;

&:checked {
  background-color: #000;
  border: 5px solid transparent; 
//   border-radius: 50%;
  border-image: linear-gradient(170deg, #f80606ff 50%, #fff 50%) 1;
}


