import { useEffect, useState } from "react";
import styled, { keyframes } from "styled-components";

// === Styled Components ===
const Card = styled.div`
  height: auto;
  margin: 0 auto;
  border-radius: 10px;
  display: flex;
  flex-direction: column;
  justify-content: space-around;
  align-items: center;
  flex-wrap: wrap;
  background: linear-gradient(-45deg, #e3f5fd, #c9e9fa, #e3f5fd);
`;

const PokemonImageTypeFrame = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-around;
  align-items: center;
  flex-wrap: wrap;
  gap: 6px;
  padding: 15px;
  margin: 5px;
`;

const PokemonImageType = styled.img`
  width: 30px;
  height: 30px;
`;

const Arrow = styled.span`
  font-size: 30px;
  font-weight: bold;
  color: #000;
  margin: 0 10px;
  display: flex;
  align-items: center;
`;

const spin = keyframes`
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
`;

const Spinner = styled.div`
  border: 6px solid #f3f3f3;
  border-top: 6px solid #3498db;
  border-radius: 50%;
  width: 60px;
  height: 60px;
  animation: ${spin} 1s linear infinite;
  margin: 50px auto;
`;

// === Helpers ===
function normalizeNumber(value) {
  if (value === undefined || value === null) return null;
  const s = String(value);
  const digits = s.replace(/\D/g, "");
  if (!digits) return null;
  return String(parseInt(digits, 10));
}

function pokeKeyFromHome(p) {
  return normalizeNumber(p?.number) ?? normalizeNumber(p?.id);
}

function imageIdForSrc(obj) {
  if (obj == null) return "";
  if (obj.id !== undefined && obj.id !== null) return String(obj.id);
  if (obj.number) {
    const raw = String(obj.number).replace(/^#/, "");
    if (raw) return raw;
  }
  return normalizeNumber(obj.number) || "";
}

// === Componente Principal ===
function FrameTypes({ pokemon }) {
  if (!pokemon) return <Spinner />;

  return (
    <Card>
      <PokemonImageTypeFrame>
        {(Array.isArray(pokemon.type) ? pokemon.type.slice(0, 2) : [pokemon.type])
          .filter(Boolean)
          .map((type) => (
            <PokemonImageType
              key={type}
              src={`http://localhost:8000/type/${encodeURIComponent(type)}.png`}
              alt={String(type)}
            />
          ))}
      </PokemonImageTypeFrame>
    </Card>
  );
}

export default FrameTypes;
