import { useEffect, useState } from 'react';
import styled, { keyframes } from "styled-components";

const PokeEvolveFrame = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-around;
  align-items: center;
  flex-wrap: wrap;
  gap: "6px";
  padding: 15px;
  margin: 5px;
`;
const PokemonImgEvo = styled.img`
  width: 20px;
  height: 20px; 
`;

const spin = keyframes`
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
`;

const Spinner = styled.div`
  border: 6px solid #f3f3f3;
  border-top: 6px solid #3498db;
  border-radius: 50%;
  width: 60px;
  height: 60px;
  animation: ${spin} 1s linear infinite;
  margin: 50px auto;
`;

function EvolvePokemons() {
  const [pokemons, setPokemons] = useState([]);
  const [evolve, setPokemonsEvolve] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    async function fetchPokemons() {
      try {
        const response = await fetch("http://localhost:8000/home");
        if (!response.ok) throw new Error("Erro ao buscar pokemons");
        const data = await response.json();
        setPokemons(data);
      } catch (err) {
        console.error("Falha no fetch:", err.message);
        setPokemons([]);
      } finally {
        setLoading(false);
      }
    }
    fetchPokemons();
  }, []);

  useEffect(() => {
    async function fetchPokemonsEvolve() {
      try {
        const response = await fetch("http://localhost:8000/evolve");
        if (!response.ok) throw new Error("Erro ao buscar pokemons");
        const data = await response.json();
        setPokemonsEvolve(data);
      } catch (err) {
        console.error("Falha no fetch:", err.message);
        setPokemons([]);
      } finally {
        setPokemonsEvolve(false);
      }
    }
    fetchPokemonsEvolve();
  }, []);

  return (
    <PokeEvolveFrame>
      {(Array.isArray(pokemon.type) ? pokemon.type.slice(0, 2) : [pokemon.type])
        .filter(Boolean)
        .map((type) => (

          <PokemonImgEvo
            src={`http://localhost:8000/evolve/${pokemons.id}/img`}
            alt={pokemons.name}
          />

        ))}
    </PokeEvolveFrame>

    // <PokeEvolveFrame>
    //   {/* {pokemon.evolves ? "Evolui" : "Não evolui"} */}

    //   <PokemonImageEvo>
    //     src={`http://localhost:8000/evolve/${pokemons.id}/img`}
    //     alt={pokemons.name}
    //   </PokemonImageEvo>






    // </PokeEvolveFrame>
  );
}
export default EvolvePokemons;
