import { useEffect, useState } from 'react';
import styled from "styled-components";

const Card = styled.div`
    // width: 100%;
    height: 100%;
    max-width: 80%;
    max-height: 100%;
    // padding: 25px 20px;
    margin: 0 auto; 
    background-color: #FFF;
    box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
    border-radius: 10px;
    display: flex;
    justify-content: space-around;
    align-items: center;
    

    
`;

const PokemonImage = styled.img`
    width: 150px;
`;

const PokemonCard = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    padding: 12px;
    margin: 10px;
    border-radius: 8px;
    background: #f5f5f5;
    box-shadow: 0 2px 6px rgba(0,0,0,0.1);
`;

function CardPokemons() {
    const [pokemons, setPokemons] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        async function fetchPokemons() {
            try {
                const response = await fetch("http://localhost:8000/home");
                if (!response.ok) throw new Error("Erro ao buscar pokemons");
                const data = await response.json();
                setPokemons(data);
            } catch (err) {
                setError(err.message);
            } finally {
                setLoading(false);
            }
        }
        fetchPokemons();
    }, []);

    // if (loading) return <p>Carregando pokémons...</p>;
    // if (error) return <p>Erro: {error}</p>;

    return (
        <Card>
            {pokemons.map(pokemon => (
                <PokemonCard key={pokemon.id}>
                    <h3 style={{ textTransform: "uppercase" }}>{pokemon.name}</h3>
                    <PokemonImage
                        src={pokemon.image || `http://localhost:8000/home/${pokemon.id}/img`}
                        alt={pokemon.name}
                    />
                    <p>Tipo: {pokemon.type.join(", ")}</p>
                    <p>{pokemon.evolves ? "Evolui" : "Não evolui"}</p>
                </PokemonCard>
            ))}
        </Card>
    )
}

export default CardPokemons;
