import { useEffect, useState } from 'react';
import styled from "styled-components";

const PokeEvolveFrame = styled.div`
  display: flex;
  flex-direction: column;
  gap: 15px;
  padding: 15px;
`;

const EvoLine = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
  gap: 10px;
`;

const PokeImgEvo = styled.img`
  width: 60px;
  height: 60px; 
`;

function EvolvePokemons() {
  const [evolves, setEvolves] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    async function fetchEvolves() {
      try {
        const response = await fetch("http://localhost:8000/evolve"); // rota singular
        if (!response.ok) throw new Error("Erro ao buscar evoluções");
        const data = await response.json();
        setEvolves(data);
      } catch (err) {
        console.error("Falha no fetch /evolve:", err.message);
        setEvolves([]);
      } finally {
        setLoading(false);
      }
    }

    fetchEvolves();
  }, []);

  if (loading) {
    return <p>Carregando...</p>;
  }

  return (
    <PokeEvolveFrame>
      {evolves.map((item) => (
        <EvoLine key={item.id}>
          {/* Pokémon base */}
          <PokeImgEvo
            src={`http://localhost:8000/home/${item.pokemon.number}/img`}
            alt={item.pokemon.name}
          />

          {/* Suas evoluções */}
          {item.evolve.map((ev) => (
            <PokeImgEvo
              key={ev.number}
              src={`http://localhost:8000/home/${ev.number}/img`}
              alt={ev.name}
            />
          ))}
        </EvoLine>
      ))}
    </PokeEvolveFrame>
  );
}

export default EvolvePokemons;
