import styled from 'styled-components';

const InputCxBx = styled.input.attrs({ type: "checkbox" })`
  appearance: none;
  cursor: pointer;
  width: 20px;
  height: 20px;

  border: 2px solid #fff;
  border-radius: 50%;
  display: inline-block;
  position: relative;
  background: #ffffff4c;

  // &::before {
  //     content: '';
  //   position: absolute;
  //   top: -2px;
  //   left: -2px;
  //   right: -2px;
  //   bottom: -2px;
  //   border-radius: 50%;
  //   background: linear-gradient(170deg, #f80606, #fff);
  //   z-index: -1;
  //   opacity: 0; /* invisível quando não marcado */
  //   transition: opacity 0.5s ease, transform 0.5s ease;
  // }
  transition: 
    background-color 0.5s ease, 
    border 1s ease, 
    border-radius 50%;
    // border-radius 1s ease, 
    transform 0.3s ease;

  &:checked {
    background-color: #000;
    border: 5px solid;
    border-image: linear-gradient(170deg, #f80606ff 50%, #fff 50%);
    border-image-slice: 1;
  }

  //   &:checked::before {
  //   opacity: 1; /* mostra a borda gradiente */
  //   transform: scale(1.1); /* efeito circular crescente */
  // }
`;

export default InputCxBx;
