import styled from 'styled-components';
import { imagens } from '../../img/pokemons';
import { Titulo } from '../Titulo';
// import CardPokemons from '../CardPokemons';
// import CardRecomenda from '../CardRecomenda';

const QuadroPrincipalContainer = styled.section`
    background-color: #EBECEE;
    padding-bottom: 20px;
    display: flex;
    flex-direction: column;
    margin-left: 20%;
    margin-right: 20%;
`;

const ContainerCards = styled.div`
    margin-top: 30px;
    display: flex;
    width: 100%;
    justify-content: center;
    cursor: pointer;
`;

function QuadroPrincipal() {
    return (
        <QuadroPrincipalContainer>
            <Titulo cor="#EB9B00" tamanhoFonte="36px">1ยบ GEN</Titulo>
            <ContainerCards>
                {imagens.map((item, index) => (
                    <img key={index} src={item.src} alt={item.nome || `pokemon-${index}`} />
                ))}
            </ContainerCards>
            {/* <CardRecomenda
                titulo="Teste"
                subtitulo="Teste"
                descricao="teste teste teste ..."
                img={imagemLivro}
            /> */}
        </QuadroPrincipalContainer>
    );
}

export default QuadroPrincipal;
