import styled, { keyframes } from 'styled-components';

const fill = keyframes`
  0% { background-size: 0% 0%; }
  100% { background-size: 100% 100%; }
`;

const InputCxBx = styled.input.attrs({ type: "checkbox" })`
  appearance: none;
  -webkit-appearance: none;
  -moz-appearance: none;

  width: 40px;
  height: 40px;
  border-radius: 50%;
  cursor: pointer;
  display: inline-block;
  position: relative;
  background-color: #000; /* cor do interior */

  /* Simula a borda com conic-gradient */
  background-image: conic-gradient(#f80606 0deg 180deg, #fff 180deg 360deg);
  background-repeat: no-repeat;
  background-position: center;
  background-size: 0% 0%; /* inicia “fechada” */
  transition: background-size 2s ease;

  &:checked {
    background-size: 100% 100%; /* “abre” a borda */
  }

  /* círculo interno preto */
  &::after {
    content: "";
    position: absolute;
    top: 4px;
    left: 4px;
    width: 32px;
    height: 32px;
    border-radius: 50%;
    background: #000;
  }
`;

export default InputCxBx;
