import { useEffect, useState } from 'react';
import styled, { keyframes } from "styled-components";

const Card = styled.div`
  height: auto;
  margin: 0 auto; 
  background-color: #e3f5fd;
  border-radius: 10px;
  display: flex;
  justify-content: space-around;
  align-items: center;
  flex-wrap: wrap;
`;

const PokemonCard = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 15px;
  margin: 5px;
  border-radius: 8px;
  background: #fff;
  box-shadow: 0 2px 6px rgba(0,0,0,0.1);
`;

const PokeNumber = styled.p`
  font-size: 20px;
  font-weight: 700;
  color: #333;
`;

const PokemonImage = styled.img`
  width: 200px;
  height: 150px;
`;

const PokeName = styled.p`
  font-size: 20px;
  font-weight: 700;
  color: #333;
  text-transform: uppercase;
`;

const PokemonImageTypeFrame = styled.div`
  display: flex;
  justify-content: center;
  gap: 6px;
  margin: 10px 0;
`;

const PokemonImageType = styled.img`
  width: 20px;
  height: 20px; 
`;

const EvoLine = styled.div`
  display: flex;
  align-items: center;
  gap: 8px;
  margin-top: 8px;
`;

const PokeImgEvo = styled.img`
  width: 60px;
  height: 60px; 
`;

const Arrow = styled.span`
  font-size: 18px;
  font-weight: bold;
`;

const spin = keyframes`
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
`;

const Spinner = styled.div`
  border: 6px solid #f3f3f3;
  border-top: 6px solid #3498db;
  border-radius: 50%;
  width: 60px;
  height: 60px;
  animation: ${spin} 1s linear infinite;
  margin: 50px auto;
`;

// normaliza "#001" -> "1"
function normalizeNumber(value) {
  if (!value) return null;
  return String(parseInt(String(value).replace(/\D/g, ""), 10));
}

function CardPokemons() {
  const [pokemons, setPokemons] = useState([]);
  const [evolves, setEvolves] = useState([]);
  const [pokemonstype, setPokemonsType] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    async function fetchData() {
      try {
        const [resPokemons, resTypes, resEvolves] = await Promise.all([
          fetch("http://localhost:8000/home"),
          fetch("http://localhost:8000/type"),
          fetch("http://localhost:8000/evolve"),
        ]);

        if (!resPokemons.ok) throw new Error("Erro ao buscar pokemons");
        if (!resTypes.ok) throw new Error("Erro ao buscar types");
        if (!resEvolves.ok) throw new Error("Erro ao buscar evoluções");

        setPokemons(await resPokemons.json());
        setPokemonsType(await resTypes.json());
        setEvolves(await resEvolves.json());

      } catch (err) {
        console.error("Falha no fetch:", err.message);
        setPokemons([]);
        setPokemonsType([]);
        setEvolves([]);
      } finally {
        setLoading(false);
      }
    }

    fetchData();
  }, []);

  if (loading) return <Spinner />;
  if (!pokemons.length) return <Spinner />;

  return (
    <Card>
      {pokemons.map((pokemon) => {
        const pokeKey = normalizeNumber(pokemon.number) || normalizeNumber(pokemon.id);

        // Encontrar a cadeia de evolução à qual esse Pokémon pertence
        const chain = evolves.find(evo =>
          normalizeNumber(evo.pokemon.number) === pokeKey ||
          evo.evolve.some(ev => normalizeNumber(ev.number) === pokeKey)
        );

        return (
          <PokemonCard key={pokemon.id ?? pokemon.number}>
            <PokeNumber>#{pokemon.id ?? pokemon.number}</PokeNumber>

            <PokemonImage
              src={`http://localhost:8000/home/${pokeKey}/img`}
              alt={pokemon.name}
            />

            <PokeName>{pokemon.name}</PokeName>

            <PokemonImageTypeFrame>
              {(Array.isArray(pokemon.type) ? pokemon.type.slice(0, 2) : [pokemon.type])
                .filter(Boolean)
                .map((type) => (
                  <PokemonImageType
                    key={type}
                    src={`http://localhost:8000/type/${encodeURIComponent(type)}.png`}
                    alt={String(type)}
                  />
                ))}
            </PokemonImageTypeFrame>

            {chain && (
              <EvoLine>
                {/* Pokémon base da cadeia */}
                <span style={{ display: "flex", alignItems: "center", gap: 4 }}>
                  <PokeImgEvo
                    src={`http://localhost:8000/home/${normalizeNumber(chain.pokemon.number)}/img`}
                    alt={chain.pokemon.name}
                  />
                  <span style={{ fontSize: 12 }}>{chain.pokemon.name}</span>
                </span>

                {/* Evoluções subsequentes */}
                {chain.evolve.map((ev, idx) => (
                  <span key={ev.number} style={{ display: "flex", alignItems: "center", gap: 4 }}>
                    <Arrow>→</Arrow>
                    <PokeImgEvo
                      src={`http://localhost:8000/home/${normalizeNumber(ev.number)}/img`}
                      alt={ev.name}
                    />
                    <span style={{ fontSize: 12 }}>{ev.name}</span>
                  </span>
                ))}
              </EvoLine>
            )}
          </PokemonCard>
        );
      })}
    </Card>
  );
}

export default CardPokemons;
