import { useEffect, useState } from 'react';
import styled from 'styled-components';
import { imagens } from '../../img/pokemons';
import { Titulo } from '../Titulo';
import CardPokemon from '../CardPokemon';
import CardRecomenda from '../CardRecomenda';


const QuadroPrincipalContainer = styled.section`
    background-color: #EBECEE;
    padding-bottom: 20px;
    display: flex;
    flex-direction: column;

    margin-left: 20%;
    margin-right: 20%;
`
const Card = styled.div`
    margin-top: 30px;
    display: flex;
    width: 100%;
    justify-content: center;
    cursor: pointer;
`

function QuadroPrincipal() {
    return (
        <QuadroPrincipalContainer>
            <Titulo or={"#EB9B00"} tamanhoFonte={"36px"}>1ยบ GEN</Titulo>
            <Card>
                {imagens.map(item => (
                    <img src={item.src} alt='img' />
                ))}
            </Card>
            {/* <CardRecomenda
                titulo="Teste"
                subtitulo="Teste"
                descricao="teste teste teste ..."
                img={imagemItem}
            /> */}
        </QuadroPrincipalContainer>
    )
}

export default QuadroPrincipal;
