import styled from 'styled-components';

const InputCxBx = styled.input.attrs({ type: "checkbox" })`
  appearance: none;
  width: 20px;
  height: 20px;
  border-radius: 50%;
  cursor: pointer;
  display: inline-block;
  position: relative;
  background: #ffffff4c;
  border: 2px solid transparent; /* base neutra */
  background-clip: padding-box;

  /* borda gradiente usando pseudo-elemento */
  &::before {
    content: '';
    position: absolute;
    top: -2px;
    left: -2px;
    right: -2px;
    bottom: -2px;
    border-radius: 50%;
    background: linear-gradient(170deg, #f80606, #fff);
    z-index: -1;
    opacity: 0; /* invisível quando não marcado */
    transition: opacity 0.5s ease, transform 0.5s ease;
  }

  transition: background-color 0.5s ease, transform 0.3s ease;

  &:checked {
    background-color: #000;
  }

  &:checked::before {
    opacity: 1; /* mostra a borda gradiente */
    transform: scale(1.1); /* efeito circular crescente */
  }
`;

export default InputCxBx;
