import { useEffect, useState } from 'react';
import styled from "styled-components";

const PokeEvolveFrame = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-around;
  align-items: center;
  flex-wrap: wrap;
  gap: 6px;
  padding: 15px;
  margin: 5px;
`;

const PokeImgEvo = styled.img`
  width: 60px;
  height: 60px; 
`;

function EvolvePokemons() {
  const [pokemons, setPokemons] = useState([]);
  const [evolves, setEvolves] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    async function fetchPokemons() {
      try {
        const response = await fetch("http://localhost:8000/home");
        if (!response.ok) throw new Error("Erro ao buscar pokemons");
        const data = await response.json();
        setPokemons(data);
      } catch (err) {
        console.error("Falha no fetch:", err.message);
        setPokemons([]);
      } finally {
        setLoading(false);
      }
    }
    fetchPokemons();
  }, []);

  if (loading) {
    return <p>Carregando...</p>;
  }

  return (
    <PokeEvolveFrame>
      {pokemons.map((pokemon) => (
        <div key={pokemon.id}>
          {/* Sempre mostra o Pokémon base */}
          <PokeImgEvo
            src={`http://localhost:8000/home/${pokemon.id}/img`}
            alt={pokemon.name}
          />

          {/* Verifica condição */}
          {pokemon.evolve === true && (
            <span style={{ fontSize: "14px", marginLeft: "6px" }}>
              Evolui!
            </span>
          )}
        </div>
      ))}
    </PokeEvolveFrame>
  );
}

export default EvolvePokemons;
