import React from "react";

/**
 * PokedexGrid
 * - Largura centralizada ocupando 80% (w-4/5 + mx-auto)
 * - Grade com 6 colunas fixas
 * - 151 cards (1 a 151) por padrão
 * - Cada card exibe: imagem (200x250), número, nome, tipo(s), evolução
 *
 * Props opcionais:
 *  - items?: Array<{
 *      numero: number;        // 1..151
 *      nome: string;          // ex.: "bulbasaur"
 *      tipo?: string[];       // ex.: ["planta", "venenoso"]
 *      evolui?: boolean;      // true/false
 *      imagem?: string;       // url
 *    }>
 * Se não for passado, gera automaticamente 1..151 com imagem oficial.
 */

function zeroPad(n, size = 3) {
  return String(n).padStart(size, "0");
}

function makeDefaultItems() {
  // Usa as artes oficiais do repositório público da PokéAPI
  return Array.from({ length: 151 }, (_, i) => {
    const number = i + 1;
    return {
      number,
      nome: `pokemon ${zeroPad(number)}`,
      tipo: [],
      evolui: undefined,
      imagem: `https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/other/official-artwork/${number}.png`,
    };
  });
}

function Badge({ children }) {
  return (
    <span className="inline-block rounded-full px-2 py-0.5 text-xs border">
      {children}
    </span>
  );
}

function Card({ item }) {
  const { number, nome, tipo = [], evolui, imagem } = item;
  return (
    <div className="rounded-2xl shadow-md border p-3 flex flex-col items-center gap-2 bg-white">
      <div className="w-[200px] h-[250px] overflow-hidden rounded-xl flex items-center justify-center bg-gray-50">
        {/* A imagem mantém 200x250 via container; object-contain evita cortes */}
        <img
          src={imagem}
          alt={nome || `pokemon ${zeroPad(number)}`}
          className="w-full h-full object-contain"
          width={200}
          height={250}
          loading="lazy"
        />
      </div>

      <div className="w-full text-center">
        <p className="text-sm text-gray-500">#{zeroPad(number)}</p>
        <h3 className="text-base font-semibold capitalize">{nome}</h3>
      </div>

      <div className="flex flex-wrap gap-1 justify-center">
        {tipo && tipo.length > 0 ? (
          tipo.map((t, idx) => <Badge key={idx}>{t}</Badge>)
        ) : (
          <span className="text-xs text-gray-400">tipo não informado</span>
        )}
      </div>

      <div>
        {typeof evolui === "boolean" ? (
          <Badge>{evolui ? "evolui" : "não evolui"}</Badge>
        ) : (
          <span className="text-xs text-gray-400">evolução não informada</span>
        )}
      </div>
    </div>
  );
}

export default function PokedexGrid({ items }) {
  const data = Array.isArray(items) && items.length > 0 ? items : makeDefaultItems();

  return (
    <div className="w-4/5 mx-auto py-6">
      <div className="grid grid-cols-6 gap-4">
        {data.slice(0, 151).map((item) => (
          <Card key={item.number} item={item} />
        ))}
      </div>
    </div>
  );
}

/**
 * Exemplo de uso:
 *
 * import PokedexGrid from "./PokedexGrid";
 *
 * function App() {
 *   // Você pode montar seu array items com dados reais (nome/tipo/evolui/imagem)
 *   return <PokedexGrid />; // sem props: gera 1..151 com imagens oficiais
 * }
 */
