import { useEffect, useState } from 'react';
import styled from "styled-components";

const PokeEvolveFrame = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-around;
  align-items: center;
  flex-wrap: wrap;
  gap: 6px;
  padding: 15px;
  margin: 5px;
`;

const PokeImgEvo = styled.img`
  width: 60px;
  height: 60px; 
`;

function EvolvePokemons() {
  const [pokemons, setPokemons] = useState([]);
  const [evolves, setEvolves] = useState([]);
  const [loading, setLoading] = useState(true);

  // useEffect(() => {
  //   async function fetchPokemons() {
  //     try {
  //       const response = await fetch("http://localhost:8000/home");
  //       if (!response.ok) throw new Error("Erro ao buscar pokemons");
  //       const data = await response.json();
  //       setPokemons(data);
  //     } catch (err) {
  //       console.error("Falha no fetch:", err.message);
  //       setPokemons([]);
  //     } finally {
  //       setLoading(false);
  //     }
  //   }
  //   fetchPokemons();
  // }, []);


    useEffect(() => {
    async function fetchPokemons() {
      try {
        const response = await fetch("http://localhost:8000/home");
        if (!response.ok) throw new Error("Erro ao buscar pokemons");
        const data = await response.json();
        setPokemons(data);
      } catch (err) {
        console.error("Falha no fetch /home:", err.message);
        setPokemons([]);
      }
    }

    async function fetchEvolves() {
      try {
        const response = await fetch("http://localhost:8000/evolves");
        if (!response.ok) throw new Error("Erro ao buscar evoluções");
        const data = await response.json();
        setEvolves(data);
      } catch (err) {
        console.error("Falha no fetch /evolves:", err.message);
        setEvolves([]);
      }
    }

    Promise.all([fetchPokemons(), fetchEvolves()]).finally(() =>
      setLoading(false)
    );
  }, []);

  if (loading) {
    return <p>Carregando...</p>;
  }

  return (
    <PokeEvolveFrame>
      {pokemons.map((pokemon) => {
        // Busca o registro de evolução correspondente
        const evoData = evolves.find((e) => e.pokemon.number === pokemon.number);

            {/* Pokémon base */}
            <PokeImgEvo
              src={`http://localhost:8000/home/${pokemon.id}/img`}
              alt={pokemon.name}
            />

            {/* Se tiver evolução, renderiza */}
            {pokemon.evolve === true && evoData && (
              <>
                {evoData.evolve.map((evo) => (
                  <PokeImgEvo
                    key={evo.number}
                    src={`http://localhost:8000/home/${evo.number}/img`}
                    alt={evo.name}
                  />
                ))}
              </>
            )}

      })}
    </PokeEvolveFrame>
  );
}

export default EvolvePokemons;
