const InputCxBx = styled.input.attrs({ type: "checkbox" })`
  appearance: none;
  -webkit-appearance: none;
  -moz-appearance: none;
  
  cursor: pointer;
  width: 24px;
  height: 24px;
  border-radius: 50%;
  display: inline-block;
  position: relative;

  border: 5px solid transparent;
  border-image: linear-gradient(170deg, #f80606ff 50%, #fff 50%) 1;

  /* fundo transparente por padrão */
  background: transparent;

  &::after {
    content: "";
    position: absolute;
    top: 50%;
    left: 50%;
    width: 0;
    height: 0;
    background: #000;
    border-radius: 50%;
    transform: translate(-50%, -50%);
    transition: all 0.5s ease;
  }

  &:checked::after {
    width: 70%;
    height: 70%;
  }
`;

