import styled, { keyframes, css } from "styled-components";
import { useEffect, useState } from "react";
import Input from "../Input";
import InputCxBx from "../InputCxBx";
import { post_fav } from "../../services/fav";

// 🔹 Animação de expansão do card
const expandCard = keyframes`
  0% { transform: scale(1); box-shadow: 0px 0px 0px rgba(0,0,0,0); }
  50% { transform: scale(1.08); box-shadow: 0px 8px 20px rgba(0,0,0,0.3); }
  100% { transform: scale(1.05); box-shadow: 0px 4px 12px rgba(0,0,0,0.25); }
`;

const fadeIn = keyframes`
  0% { opacity: 0; transform: translateY(-10px); }
  100% { opacity: 1; transform: translateY(0); }
`;

const FrameInput = styled.div``;

const PesquisaContainer = styled.section`
  color: #fff;
  width: 80%;
  padding: 40px 0;
  margin: auto;
  text-align: center;
  border-radius: 20px;
  backdrop-filter: blur(6px);
  box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.25);
  transition: all 0.3s ease;
  border: 1px solid #fff;
  display: flex;
  flex-direction: column;
`;

const CheckboxContainer = styled.div`
  margin-top: 10px;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 8px;
  label {
    font-size: 16px;
    color: #fff;
    cursor: pointer;
    user-select: none;
  }
  input[type="checkbox"] {
    width: 15px;
    height: 15px;
    cursor: pointer;
  }
`;

const Titulo = styled.h2`
  color: #fff;
  font-size: 35px;
  text-align: center;
  width: 100%;
`;

const ResultadosGrid = styled.div`
  width: 80%;
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  max-width: 1000px;
  margin: 20px auto;
  gap: 20px;
  border-radius: 20px;
`;

const Resultado = styled.div`
  background: rgba(255, 255, 255, 0.2);
  padding: 10px;
  border-radius: 20px;
  text-align: center;
  cursor: pointer;
  transition: 0.3s ease;

  p {
    margin-top: 8px;
    font-size: 15px;
    color: #fff;
  }
  img {
    width: 75px;
    height: 75px;
    object-fit: contain;
  }

  &:hover {
    border: 1px solid #fff;
    transform: scale(1.05);
  }

  ${({ hasEvolves }) =>
    hasEvolves &&
    css`
      animation: ${expandCard} 0.6s ease forwards;
    `}
`;

const EvolvesContainer = styled.div`
  margin-top: 10px;
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 15px;
  animation: ${fadeIn} 0.5s ease forwards;

  p {
    font-size: 14px;
    margin-top: 5px;
  }
`;

const PokemonImage = styled.img`
  width: 100px;
  height: 100px;
`;

const spin = keyframes`
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
`;

const Spinner = styled.div`
  border: 6px solid #f3f3f3;
  border-top: 6px solid #3498db;
  border-radius: 50%;
  width: 60px;
  height: 60px;
  animation: ${spin} 1s linear infinite;
  margin: 50px auto;
`;

// 🔧 Garante formato "001", "002", "003"
const formatNumber = (num) => num?.toString().padStart(3, "0");

function Pesquisa() {
  const [allPokemons, setAllPokemons] = useState([]);
  const [pokemons, setPokemons] = useState([]);
  const [showEvolves, setShowEvolves] = useState(false);
  const [evolvesList, setEvolvesList] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    async function fetchPokemons() {
      try {
        const response = await fetch("http://localhost:8000/home");
        if (!response.ok) throw new Error("Erro ao buscar pokemons");
        const data = await response.json();
        setAllPokemons(data);
      } catch (err) {
        console.error("Falha no fetch:", err.message);
        setAllPokemons([]);
      } finally {
        setLoading(false);
      }
    }
    fetchPokemons();
  }, []);

  useEffect(() => {
    async function fetchEvolves() {
      try {
        const response = await fetch("http://localhost:8000/evolves");
        if (!response.ok) throw new Error("Erro ao buscar evoluções");
        const data = await response.json();
        setEvolvesList(data);
      } catch (err) {
        console.error("Falha no fetch /evolves:", err.message);
        setEvolvesList([]);
      }
    }
    fetchEvolves();
  }, []);

  async function insert_fav(id) {
    await post_fav(id);
    alert(`Item de id:${id} inserido!`);
  }

  function handleSearch(evento) {
    const textoDigitado = evento.target.value.toLowerCase();
    if (textoDigitado.trim() === "") {
      setPokemons([]);
      return;
    }

    let resultadoPesquisa = [];

    // 1️⃣ Busca no nome do Pokémon
    resultadoPesquisa = allPokemons.filter((pokemon) =>
      pokemon.name?.toLowerCase().includes(textoDigitado)
    );

    // 2️⃣ Verifica se esse Pokémon pertence a uma linha evolutiva
    evolvesList.forEach((linha) => {
      const pertenceALinha =
        linha.pokemon?.name?.toLowerCase().includes(textoDigitado) ||
        linha.evolve?.some((ev) =>
          ev.name.toLowerCase().includes(textoDigitado)
        );

      if (pertenceALinha) {
        // adiciona o Pokémon base
        const base = allPokemons.find(
          (p) => formatNumber(p.id) === linha.pokemon?.number
        );
        if (base && !resultadoPesquisa.some((r) => r.id === base.id)) {
          resultadoPesquisa.push(base);
        }

        // adiciona evoluções
        linha.evolve?.forEach((ev) => {
          const evo = allPokemons.find(
            (p) => formatNumber(p.id) === ev.number
          );
          if (evo && !resultadoPesquisa.some((r) => r.id === evo.id)) {
            resultadoPesquisa.push(evo);
          }
        });
      }
    });

    setPokemons(resultadoPesquisa);
  }

  if (loading) return <Spinner />;

  return (
    <PesquisaContainer>
      <Titulo>Pesquise por um pokemon</Titulo>
      <FrameInput>
        <Input placeholder="Pesquise por nome" onChange={handleSearch} />
        <CheckboxContainer>
          <InputCxBx
            id="evolves"
            checked={showEvolves}
            onChange={(e) => setShowEvolves(e.target.checked)}
          />
          <label htmlFor="evolves">Evolves</label>
        </CheckboxContainer>
      </FrameInput>

      {pokemons.length > 0 && (
        <ResultadosGrid>
          {pokemons.map((pokemon) => {
            // Verifica se esse pokemon pertence a alguma linha evolutiva
            const evolucao = evolvesList.find(
              (linha) =>
                linha.pokemon?.number === formatNumber(pokemon.id) ||
                linha.evolve?.some(
                  (ev) => ev.number === formatNumber(pokemon.id)
                )
            );

            return (
              <Resultado
                key={pokemon.id}
                hasEvolves={!!evolucao}
                onClick={() => insert_fav(pokemon.id)}
              >
                <PokemonImage
                  src={`http://localhost:8000/home/${formatNumber(
                    pokemon.id
                  )}/img`}
                  alt={pokemon.name}
                />
                <p>{pokemon.name}</p>

                {/* Sempre mostra as evoluções se houver linha */}
                {evolucao && (
                  <EvolvesContainer>
                    <div>
                      <PokemonImage
                        src={`http://localhost:8000/home/${evolucao.pokemon.number}/img`}
                        alt={evolucao.pokemon.name}
                      />
                      <p>{evolucao.pokemon.name}</p>
                    </div>
                    {evolucao.evolve?.map((ev) => (
                      <div key={ev.number}>
                        <PokemonImage
                          src={`http://localhost:8000/home/${ev.number}/img`}
                          alt={ev.name}
                        />
                        <p>{ev.name}</p>
                      </div>
                    ))}
                  </EvolvesContainer>
                )}
              </Resultado>
            );
          })}
        </ResultadosGrid>
      )}
    </PesquisaContainer>
  );
}

export default Pesquisa;
