import { useEffect, useState } from 'react';
import styled, { keyframe } from 'styled-components';
import { get_fav, post_fav, del_fav } from '../services/fav';
import FrameFav from '../componentes/FrameTypes';

const AppContainer = styled.div`
  width: 100vw;
  height: 100vh;
  border-radius: 20px;
  background: linear-gradient(100deg,  #d4e5feff 0%,  #3a6ea5 50%);
`

const Titulo = styled.h2`
    color: #FFF;
    font-size: 36px;
    text-align: center;
    width: 100%;
    padding-top: 35px
`

