import { useEffect, useState } from 'react';
import styled from "styled-components";

const PokeEvolveFrame = styled.div`
  display: flex;
  flex-direction: column;
  gap: 20px;
  padding: 15px;
  margin: 5px;
`;

const PokemonRow = styled.div`
  display: flex;
  align-items: center;
  gap: 8px;
`;

const PokeImgEvo = styled.img`
  width: 60px;
  height: 60px; 
`;

const Arrow = styled.span`
  font-size: 18px;
  font-weight: bold;
  color: #333;
`;

function EvolvePokemons() {
  const [evolves, setEvolves] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    async function fetchEvolves() {
      try {
        const response = await fetch("http://localhost:8000/evolves");
        if (!response.ok) throw new Error("Erro ao buscar evoluções");
        const data = await response.json();
        setEvolves(data);
      } catch (err) {
        console.error("Falha no fetch /evolves:", err.message);
        setEvolves([]);
      } finally {
        setLoading(false);
      }
    }
    fetchEvolves();
  }, []);

  if (loading) {
    return <p>Carregando...</p>;
  }

  return (
    <PokeEvolveFrame>
      {evolves.map((evoSet) => (
        <PokemonRow key={evoSet.pokemon.number}>
          {/* Pokémon base */}
          <PokeImgEvo
            src={`http://localhost:8000/home/${evoSet.pokemon.number}/img`}
            alt={evoSet.pokemon.name}
          />

          {/* Evoluções */}
          {evoSet.evolve.map((evo) => (
            <span key={evo.number} style={{ display: "flex", alignItems: "center", gap: "8px" }}>
              <Arrow>→</Arrow>
              <PokeImgEvo
                src={`http://localhost:8000/home/${evo.number}/img`}
                alt={evo.name}
              />
            </span>
          ))}
        </PokemonRow>
      ))}
    </PokeEvolveFrame>
  );
}

export default EvolvePokemons;
