import { useEffect, useState } from 'react';
import styled, { keyframes } from "styled-components";

const PokeEvolveFrame = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-around;
  align-items: center;
  flex-wrap: wrap;
  gap: 6px;
  padding: 15px;
  margin: 5px;
`;

const PokemonImgEvo = styled.img`
  width: 60px;
  height: 60px; 
`;

const spin = keyframes`
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
`;

const Spinner = styled.div`
  border: 6px solid #f3f3f3;
  border-top: 6px solid #3498db;
  border-radius: 50%;
  width: 60px;
  height: 60px;
  animation: ${spin} 1s linear infinite;
  margin: 50px auto;
`;

function EvolvePokemons() {
  const [pokemons, setPokemons] = useState([]);
  const [evolves, setEvolves] = useState([]);
  const [loading, setLoading] = useState(true);
  const [loadingEvolve, setLoadingEvolve] = useState(true);

  useEffect(() => {
    async function fetchPokemons() {
      try {
        const response = await fetch("http://localhost:8000/home");
        if (!response.ok) throw new Error("Erro ao buscar pokemons");
        const data = await response.json();
        setPokemons(data);
      } catch (err) {
        console.error("Falha no fetch:", err.message);
        setPokemons([]);
      } finally {
        setLoading(false);
      }
    }
    fetchPokemons();
  }, []);

  useEffect(() => {
    async function fetchPokemonsEvolve() {
      try {
        const response = await fetch("http://localhost:8000/evolve");
        if (!response.ok) throw new Error("Erro ao buscar evoluções");
        const data = await response.json();
        setEvolves(data);
      } catch (err) {
        console.error("Falha no fetch:", err.message);
        setEvolves([]);
      } finally {
        setLoadingEvolve(false);
      }
    }
    fetchPokemonsEvolve();
  }, []);

  if (loading || loadingEvolve) {
    return <Spinner />;
  }

  return (
    <PokeEvolveFrame>
      {evolves.map((evo) => (
        <PokemonImgEvo
          key={evo.number}
          src={`http://localhost:8000/home/${evo.number}/img`}
          alt={evo.name}
        />
      ))}
    </PokeEvolveFrame>
  );
}

export default EvolvePokemons;
