import styled from "styled-components";

const InputCxBx = styled.input.attrs({ type: "checkbox" })`
  appearance: none;
  -webkit-appearance: none;
  -moz-appearance: none;
  
  cursor: pointer;
  width: 20px;
  height: 20px;

  border: 2px solid #fff;
  border-radius: 50%;
  display: inline-block;

  background: #ffffff4c;

  transition:
    background-color 0.5s ease, 
    border 1s ease,
    transform 0.3s ease;

  &:checked {
    height: 20px;
    width: 5px;
    background-color: #000;
    border-radius: 50%;
    border-block-style: solid;
    border-block-color: red white;
    border-block-width: 6px;

    // border: 5px solid;
    // border-image: linear-gradient(170deg, #f80606ff 50%, #fff 50%) 1;
    // transform: scale(1,1)
  }
`;

export default InputCxBx;
