import { useEffect, useState } from 'react';
import styled from "styled-components";
import { Titulo } from "../Titulo";

const Card = styled.div`
    align-items: center;
    background-color: #FFF;
    box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
    border-radius: 10px;
    display: flex;
    margin: 0 auto;
    max-width: 600px;
    padding: 25px 20px;
    justify-content: space-around;
    width: 100%;  
`;

const Botao = styled.button`
    background-color: #EB9B00;
    color: #FFF;
    padding: 10px 0px;
    font-size: 16px;
    border: none;
    font-weight: 900;
    display: block;
    text-align: center;
    width: 150px;
    &:hover {
        cursor: pointer;
    }
`;

const PokemonImage = styled.img`
    width: 150px;
`;

const PokemonCard = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    padding: 12px;
    margin: 10px;
    border-radius: 8px;
    background: #f5f5f5;
    box-shadow: 0 2px 6px rgba(0,0,0,0.1);
`;

function CardPokemons() {
    const [pokemons, setPokemons] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        async function fetchPokemons() {
            try {
                const response = await fetch("http://localhost:8000/home");
                if (!response.ok) throw new Error("Erro ao buscar pokemons");
                const data = await response.json();
                setPokemons(data);
            } catch (err) {
                setError(err.message);
            } finally {
                setLoading(false);
            }
        }
        fetchPokemons();
    }, []);

    if (loading) return <p>Carregando pokÃ©mons...</p>;
    // if (error) return <p>Erro: {error}</p>;

    return (
        <Card>
            {/* <Botao> ðŸ‘Œ </Botao> */}

            {pokemons.map(pokemon => (
                <PokemonCard key={pokemon.id}>
                    <h3 style={{ textTransform: "uppercase" }}>{pokemon.name}</h3>
                    <PokemonImage
                        src={pokemon.image || `http://localhost:8000/home/${pokemon.id}/img`}
                        alt={pokemon.name}
                    />
                    <p>Tipo: {pokemon.type.join(", ")}</p>
                    <p>{pokemon.evolves ? "Evolui" : "NÃ£o evolui"}</p>
                </PokemonCard>
            ))}
        </Card>
    )
}

export default CardPokemons;
