import { useEffect, useState } from 'react';
import styled, { keyframes } from 'styled-components';
// import { get_fav, post_fav, del_fav } from '../services/fav';

const spin = keyframes`
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
`;
const FrameDef = styled.div`
border: 2px solid #000;
width: 80%;
height: auto;

  display: flex;
  flex-direction: column;
  gap: 20px;
  margin-left: auto;
  margin-right: auto;
  // padding: 5px;
`;

const FrameRow = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  // gap: 10px;
  background-color: #ffffff3c;
  padding: 10px;
  border-radius: 20px;
  backdrop-filter: blur(10px);
  -webkit-backdrop-filter: blur(10px);
  box-shadow: 0 8px 20px rgba(0, 0, 0, 0.15);
`;

const Spinner = styled.div`
  border: 6px solid #f3f3f3;
  border-top: 6px solid #3498db;
  border-radius: 20px;
  width: 60px;
  height: 60px;
  animation: ${spin} 1s linear infinite;
  margin: 50px auto;
`;

function buildImgSrc(id) {
  return `http://localhost:8000/home/${id}.png`;
}

function FrameFavs() {
  const [favs, setFavs] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    async function fetchFavs() {
      try {
        // 1) Buscar lista de tipos (com imagens corretas)
        const resFavs = await fetch("http://localhost:8000/fav");
        if (!resFavs.ok) throw new Error("Erro ao buscar /favs");
        const favsData = await resFavs.json();

        // Monta dicionário 
        const favsMap = {};
        favsData.forEach((t) => {
          favsData[t.name.toLowerCase()] = `http://localhost:8000${t.url}`;
        });
        setFavs();
      } catch (err) {
        console.error("❌ Erro no fetch:", err);
        setFavs([]);
      } finally {
        setLoading(false);
      }
    }

    fetchFavs();
  }, []);


  if (loading) return <Spinner />;

  return (
    <FrameDef>
    </FrameDef>
  );
}

export default FrameFavs;
