import { useEffect, useState } from "react";
import styled, { keyframes } from "styled-components";

const FramePokeImgType = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  flex-wrap: wrap;
  gap: 10px;
  padding: 15px;
  margin: 5px;
`;

const Card = styled.div`
  height: auto;
  margin: 0 auto;
  border-radius: 10px;
  display: flex;
  flex-direction: column;
  justify-content: space-around;
  align-items: center;
  flex-wrap: wrap;
  background: linear-gradient(-45deg, #e3f5fd, #c9e9fa, #e3f5fd);
`;

const PokemonImageType = styled.img`
  width: 60px;
  height: 60px;
  object-fit: contain;
`;

const TypeTitle = styled.h3`
  text-transform: uppercase;
  font-size: 18px;
  margin: 10px 0;
`;

const spin = keyframes`
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
`;

const Spinner = styled.div`
  border: 6px solid #f3f3f3;
  border-top: 6px solid #3498db;
  border-radius: 50%;
  width: 60px;
  height: 60px;
  animation: ${spin} 1s linear infinite;
  margin: 50px auto;
`;

// === Componente Principal ===
function FrameTypes() {
  const [types, setTypes] = useState({});
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    async function fetchTypes() {
      try {
        // 1 - Buscar lista de tipos
        const res = await fetch("http://localhost:8000/types");
        if (!res.ok) throw new Error("Erro ao buscar tipos");
        const data = await res.json();

        const uniqueTypes = [...new Set(data.map((t) => t.type))];

        // 2 - Para cada tipo, buscar imagens dos pokémons
        const typeData = {};
        for (const type of uniqueTypes) {
          try {
            const resType = await fetch(
              `http://localhost:8000/types/${encodeURIComponent(type)}`
            );
            if (!resType.ok) throw new Error(`Erro ao buscar ${type}`);
            const imgs = await resType.json(); // supondo que o backend retorne um array de imagens
            typeData[type] = imgs;
          } catch (err) {
            console.error("Erro ao buscar imagens de", type, err.message);
            typeData[type] = [];
          }
        }

        setTypes(typeData);
      } catch (err) {
        console.error("Erro no fetch:", err.message);
        setTypes({});
      } finally {
        setLoading(false);
      }
    }

    fetchTypes();
  }, []);

  if (loading) return <Spinner />;
  if (!Object.keys(types).length) return <p>Nenhum tipo encontrado.</p>;

  return (
    <Card>
      {Object.entries(types).map(([type, imgs]) => (
        <div key={type}>
          <TypeTitle>{type}</TypeTitle>
          <PokemonImageTypeFrame>
            {imgs.map((img, idx) => (
              <PokemonImageType key={idx} src={img} alt={`${type}-${idx}`} />
            ))}
          </PokemonImageTypeFrame>
        </div>
      ))}
    </Card>
  );
}

export default FrameTypes;
