import { useEffect, useState } from "react";
import styled from "styled-components";

const FramePokeEvoGrid = styled.div`
  display: flex;
  justify-content: center;
  gap: 20px;
  flex-wrap: wrap;
  margin: 20px 0;
`;

const EvolveCard = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  border-radius: 12px;
  padding: 12px;
  background: #f8f9fa;
  box-shadow: 0 4px 6px rgba(0,0,0,0.1);
`;

const PokeImgEvo = styled.img`
  width: 120px;
  height: 120px;
  object-fit: contain;
`;

function Evolves() {
  const [evolves, setEvolves] = useState([]);
  const [pokemons, setPokemons] = useState([]); // üëà novo state
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    async function fetchData() {
      try {
        const [resEvolves, resPokemons] = await Promise.all([
          fetch("http://localhost:8000/evolves"),
          fetch("http://localhost:8000/home"),
        ]);

        if (!resEvolves.ok) throw new Error("Erro ao buscar evolu√ß√µes");
        if (!resPokemons.ok) throw new Error("Erro ao buscar pok√©mons");

        const evolvesData = await resEvolves.json();
        const pokemonsData = await resPokemons.json();

        setEvolves(evolvesData || []);
        setPokemons(pokemonsData || []);
      } catch (err) {
        console.error("Falha no fetch:", err.message);
        setEvolves([]);
        setPokemons([]);
      } finally {
        setLoading(false);
      }
    }

    fetchData();
  }, []);

  if (loading) return <p>Carregando...</p>;
  if (!Array.isArray(evolves) || evolves.length === 0)
    return <p>Nenhuma evolu√ß√£o encontrada.</p>;

  return (
    <div style={{ padding: "20px" }}>
      <p>teste</p>
      {evolves.map((group) => (
        <div key={group.group}>
          <FramePokeEvoGrid>
            {group.chain?.map((ev) => {
              // buscar os dados extras do Pok√©mon (se precisar) pelo n√∫mero/id
              const pokemonData = pokemons.find(
                (p) => p.id === ev.id || p.number === ev.number
              );

              return (
                <EvolveCard key={ev.id ?? ev.number}>
                  <PokeImgEvo
                    src={
                      ev.image ||
                      `http://localhost:8000/home/${ev.number}/img`
                    }
                    alt={ev.name}
                  />

                  {/* Exemplo: mostrar nome do Pok√©mon vindo da lista de pokemons */}
                  {pokemonData && (
                    <p style={{ marginTop: "8px" }}>
                      #{pokemonData.number} {pokemonData.name}
                    </p>
                  )}
                </EvolveCard>
              );
            })}
          </FramePokeEvoGrid>
        </div>
      ))}
    </div>
  );
}

export default Evolves;
