import { useEffect, useState } from "react";
import styled from "styled-components";

const FramePokeEvoGrid = styled.div`
  display: flex;
  justify-content: center;
  gap: 20px;
  flex-wrap: wrap;
  margin: 20px 0;
`;

const EvolveCard = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  border-radius: 12px;
  padding: 12px;
  background: #f8f9fa;
  box-shadow: 0 4px 6px rgba(0,0,0,0.1);
`;

const EvoLine = styled.div`
  display: flex;
  align-items: center;
  gap: 8px;
  margin-top: 8px;
`;

const PokeImgEvo = styled.img`
  width: 120px;
  height: 120px;
  object-fit: contain;
`;

const Arrow = styled.span`
  font-size: 18px;
  font-weight: bold;
`;

function FrameEvolves() {
  const [evolves, setEvolves] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    async function fetchEvolves() {
      try {
        const response = await fetch("http://localhost:8000/evolve");
        if (!response.ok) throw new Error("Erro ao buscar evoluções");
        const data = await response.json();
        // Filtra só os pokémons que têm evoluções
        setEvolves(data.filter(item => item.evolve && item.evolve.length > 0));
      } catch (err) {
        console.error("Falha no fetch /evolve:", err.message);
        setEvolves([]);
      } finally {
        setLoading(false);
      }
    }
    fetchEvolves();
  }, []);

  if (loading) {
    return <p>Carregando...</p>;
  }
//   return (
//     <FramePokeEvoGrid>
//       <EvolveCard>
//         <PokeImgEvo
//         />
//       </EvolveCard>
//     </FramePokeEvoGrid>
//   );
  return (
    <FramePokeEvoGrid>
      {evolves.map((item) => (
        <EvolveCard key={item.id}>
          <EvoLine>
            {item.evolve.map((ev, idx) => (
              <span key={ev.number} style={{ display: "flex", alignItems: "center", gap: 4 }}>
                {idx > 0 && <Arrow>→</Arrow>}
                <PokeImgEvo
                  src={`http://localhost:8000/home/${ev.number}/img`}
                  alt={ev.name}
                />
                <span style={{ fontSize: 12 }}>{ev.name}</span>
              </span>
            ))}
          </EvoLine>
        </EvolveCard>
      ))}
    </FramePokeEvoGrid>
  );
}
export default FrameEvolves;


