import styled, { keyframes, css } from "styled-components";
import { useEffect, useState } from "react";
import Input from "../Input";
import InputCxBx from "../InputCxBx";
import { post_fav } from "../../services/fav";

// ðŸ”¹ AnimaÃ§Ãµes
const expandCard = keyframes`
  0% { transform: scale(1); box-shadow: 0px 0px 0px rgba(0,0,0,0); }
  50% { transform: scale(1.08); box-shadow: 0px 8px 20px rgba(0,0,0,0.3); }
  100% { transform: scale(1.05); box-shadow: 0px 4px 12px rgba(0,0,0,0.25); }
`;

const fadeIn = keyframes`
  0% { opacity: 0; transform: translateY(-10px); }
  100% { opacity: 1; transform: translateY(0); }
`;

const FrameInput = styled.div``;

const PesquisaContainer = styled.section`
  color: #fff;
  width: 80%;
  padding: 40px 0;
  margin: auto;
  text-align: center;
  border-radius: 20px;
  backdrop-filter: blur(6px);
  box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.25);
  transition: all 0.3s ease;
  border: 1px solid #fff;
  display: flex;
  flex-direction: column;
`;

const CheckboxContainer = styled.div`
  margin-top: 10px;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 8px;
  label {
    font-size: 16px;
    color: #fff;
    cursor: pointer;
    user-select: none;
  }
  input[type="checkbox"] {
    width: 15px;
    height: 15px;
    cursor: pointer;
  }
`;

const Titulo = styled.h2`
  color: #fff;
  font-size: 35px;
  text-align: center;
  width: 100%;
`;

const ResultadosGrid = styled.div`
  width: 80%;
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  max-width: 1000px;
  margin: 20px auto;
  gap: 20px;
  border-radius: 20px;
`;

const Resultado = styled.div`
  background: rgba(255, 255, 255, 0.2);
  padding: 10px;
  border-radius: 20px;
  text-align: center;
  transition: 0.3s ease;

  p {
    margin-top: 8px;
    font-size: 15px;
    color: #fff;
  }
  img {
    width: 75px;
    height: 75px;
    object-fit: contain;
  }

  &:hover {
    border: 1px solid #fff;
    transform: scale(1.05);
  }

  ${({ hasEvolves }) =>
    hasEvolves &&
    css`
      animation: ${expandCard} 0.6s ease forwards;
    `}
`;

const EvolvesContainer = styled.div`
  margin-top: 10px;
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 15px;
  animation: ${fadeIn} 0.5s ease forwards;

  p {
    font-size: 14px;
    margin-top: 5px;
  }
`;

const PokemonImage = styled.img`
  width: 100px;
  height: 100px;
`;

const spin = keyframes`
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
`;

const Spinner = styled.div`
  border: 6px solid #f3f3f3;
  border-top: 6px solid #3498db;
  border-radius: 50%;
  width: 60px;
  height: 60px;
  animation: ${spin} 1s linear infinite;
  margin: 50px auto;
`;

// ðŸ”§ Garante formato "001"
const formatNumber = (num) => num?.toString().padStart(3, "0");

function Pesquisa() {
  const [allPokemons, setAllPokemons] = useState([]);
  const [pokemons, setPokemons] = useState([]);
  const [evolvesList, setEvolvesList] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showEvolves, setShowEvolves] = useState(false); // ðŸ”¹ adicionado

  useEffect(() => {
    async function fetchPokemons() {
      try {
        const response = await fetch("http://localhost:8000/home");
        if (!response.ok) throw new Error("Erro ao buscar pokemons");
        const data = await response.json();
        setAllPokemons(data);
      } catch (err) {
        console.error("Falha no fetch:", err.message);
        setAllPokemons([]);
      } finally {
        setLoading(false);
      }
    }
    fetchPokemons();
  }, []);

  useEffect(() => {
    async function fetchEvolves() {
      try {
        const response = await fetch("http://localhost:8000/evolves");
        if (!response.ok) throw new Error("Erro ao buscar evoluÃ§Ãµes");
        const data = await response.json();
        setEvolvesList(data);
      } catch (err) {
        console.error("Falha no fetch /evolves:", err.message);
        setEvolvesList([]);
      }
    }
    fetchEvolves();
  }, []);

  async function insert_fav(id) {
    await post_fav(id);
    alert(`Item de id:${id} inserido!`);
  }

  function handleSearch(evento) {
    const textoDigitado = evento.target.value.toLowerCase();
    if (textoDigitado.trim() === "") {
      setPokemons([]);
      return;
    }

    let resultado = [];

    // ðŸ” 1) Busca se o termo bate com alguma linha evolutiva
    evolvesList.forEach((linha) => {
      const pertenceALinha =
        linha.pokemon?.name?.toLowerCase().includes(textoDigitado) ||
        linha.evolve?.some((ev) =>
          ev.name.toLowerCase().includes(textoDigitado)
        );

      if (pertenceALinha) {
        // Garante que vamos trazer sÃ³ o POKÃ‰MON BASE
        const base = allPokemons.find(
          (p) => formatNumber(p.id) === linha.pokemon?.number
        );
        if (base && !resultado.some((r) => r.id === base.id)) {
          resultado.push(base);
        }
      }
    });

    // ðŸ” 2) Busca PokÃ©mon que nÃ£o tem linha evolutiva
    const semEvolucao = allPokemons.filter(
      (p) =>
        p.name?.toLowerCase().includes(textoDigitado) &&
        !evolvesList.some(
          (linha) =>
            linha.pokemon?.number === formatNumber(p.id) ||
            linha.evolve?.some((ev) => ev.number === formatNumber(p.id))
        )
    );

    resultado = [...resultado, ...semEvolucao];

    setPokemons(resultado);
  }

  if (loading) return <Spinner />;

  return (
    <PesquisaContainer>
      <Titulo>Pesquise por um pokemon</Titulo>
      <FrameInput>
        <Input placeholder="Pesquise por nome" onChange={handleSearch} />

        {/* ðŸ”¹ Checkbox recuperado do 1Âº cÃ³digo */}
        <CheckboxContainer>
          <InputCxBx
            id="evolves"
            checked={showEvolves}
            onChange={(e) => setShowEvolves(e.target.checked)}
          />
          <label htmlFor="evolves">Evolves</label>
        </CheckboxContainer>
      </FrameInput>

      {pokemons.length > 0 && (
        <ResultadosGrid>
          {pokemons.map((pokemon) => {
            const evolucao = evolvesList.find(
              (linha) => linha.pokemon?.number === formatNumber(pokemon.id)
            );

            return (
              <Resultado
                key={pokemon.id}
                hasEvolves={!!evolucao}
                onClick={() => insert_fav(pokemon.id)}
              >
                <PokemonImage
                  src={`http://localhost:8000/home/${formatNumber(
                    pokemon.id
                  )}/img`}
                  alt={pokemon.name}
                />
                <p>{pokemon.name}</p>

                {/* SÃ³ mostra as evoluÃ§Ãµes se o checkbox estiver marcado */}
                {evolucao && showEvolves && (
                  <EvolvesContainer>
                    {[evolucao.pokemon, ...(evolucao.evolve || [])].map(
                      (poke) => (
                        <div key={poke.number}>
                          <PokemonImage
                            src={`http://localhost:8000/home/${poke.number}/img`}
                            alt={poke.name}
                          />
                          <p>{poke.name}</p>
                        </div>
                      )
                    )}
                  </EvolvesContainer>
                )}
              </Resultado>
            );
          })}
        </ResultadosGrid>
      )}
    </PesquisaContainer>
  );
}

export default Pesquisa;
