import { useEffect, useState } from 'react';
import styled, { keyframes } from "styled-components";

const Card = styled.div`
    height: auto;
    max-width: 80%;
    margin: 0 auto; 
    background-color: #FFF;
    box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
    border-radius: 10px;
    display: flex;
    justify-content: space-around;
    align-items: center;
    flex-wrap: wrap;
`;

const PokemonImage = styled.img`
    width: 150px;
`;

const PokemonCard = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    padding: 12px;
    margin: 10px;
    border-radius: 8px;
    background: #f5f5f5;
    box-shadow: 0 2px 6px rgba(0,0,0,0.1);
`;

/* animação do spinner */
const spin = keyframes`
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
`;

const Spinner = styled.div`
  border: 6px solid #f3f3f3;
  border-top: 6px solid #3498db;
  border-radius: 50%;
  width: 60px;
  height: 60px;
  animation: ${spin} 1s linear infinite;
  margin: 50px auto;
`;

function CardPokemons() {
    const [pokemons, setPokemons] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        async function fetchPokemons() {
            try {
                const response = await fetch("http://localhost:8000/home");
                if (!response.ok) throw new Error("Erro ao buscar pokemons");
                const data = await response.json();
                setPokemons(data);
            } catch (err) {
                console.error(err.message);
            } finally {
                setLoading(false);
            }
        }
        fetchPokemons();
    }, []);

    // mostra spinner apenas enquanto carrega
    if (loading) {
        return <Spinner />;
    }

    return (
        
        <Card>
            {pokemons.map(pokemon => (
                <PokemonCard key={pokemon.id}>
                    <h3 style={{ textTransform: "uppercase" }}>{pokemon.name}</h3>
                    <p>#{pokemon.id}</p>
                    <PokemonImage
                        src={pokemon.image || `http://localhost:8000/home/${pokemon.id}/img`}
                        alt={pokemon.name}
                    />
                    <p>Tipo: {pokemon.type.join(", ")}</p>
                    <p>{pokemon.evolves ? "Evolui" : "Não evolui"}</p>
                </PokemonCard>
            ))}
        </Card>
    )
}

export default CardPokemons;
