import { useEffect, useState } from 'react';
import styled, { keyframes } from "styled-components";

const Card = styled.div`
  height: auto;
  margin: 0 auto; 
  background-color: #e3f5fd;
  border-radius: 10px;
  display: flex;
  justify-content: space-around;
  align-items: center;
  flex-wrap: wrap;
`;

const PokemonCard = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 15px;
  margin: 5px;
  border-radius: 8px;
  background: #fff;
  box-shadow: 0 2px 6px rgba(0,0,0,0.1);
`;

const PokeNumber = styled.p`
  font-size: 20px;
  font-family: 'Montserrat','Helvetica Neue',Arial,sans-serif;
  font-weight: 700;
  line-height: 1.5;
  letter-spacing: 0.5px;
  color: #333;
`;

const PokemonImage = styled.img`
  width: 200px;
  height: 150px;
`;

const PokeName = styled.p`
  font-size: 20px;
  font-family: 'Montserrat','Helvetica Neue',Arial,sans-serif;
  font-weight: 700;
  line-height: 1.5;
  letter-spacing: 0.5px;
  color: #333;
`;

const PokemonImageTypeFrame = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-around;
  align-items: center;
  flex-wrap: wrap;
  gap: 6px;
  padding: 15px;
  margin: 5px;
`;

const PokemonImageType = styled.img`
  width: 20px;
  height: 20px; 
`;

const EvoLine = styled.div`
  display: flex;
  align-items: center;
  gap: 8px;
  margin-top: 8px;
`;

const PokeImgEvo = styled.img`
  width: 60px;
  height: 60px; 
`;

const Arrow = styled.span`
  font-size: 18px;
  font-weight: bold;
`;

const spin = keyframes`
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
`;

const Spinner = styled.div`
  border: 6px solid #f3f3f3;
  border-top: 6px solid #3498db;
  border-radius: 50%;
  width: 60px;
  height: 60px;
  animation: ${spin} 1s linear infinite;
  margin: 50px auto;
`;

function CardPokemons() {
  const [pokemons, setPokemons] = useState([]);
  const [evolves, setEvolves] = useState([]);
  const [pokemonstype, setPokemonsType] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    async function fetchData() {
      try {
        // busca pokemons
        const resPokemons = await fetch("http://localhost:8000/home");
        if (!resPokemons.ok) throw new Error("Erro ao buscar pokemons");
        const pokemonsData = await resPokemons.json();
        setPokemons(pokemonsData);

        // busca types
        const resTypes = await fetch("http://localhost:8000/type");
        if (!resTypes.ok) throw new Error("Erro ao buscar types");
        const typesData = await resTypes.json();
        setPokemonsType(typesData);

        // busca evoluções
        const resEvolves = await fetch("http://localhost:8000/evolve");
        if (!resEvolves.ok) throw new Error("Erro ao buscar evoluções");
        const evolvesData = await resEvolves.json();
        setEvolves(evolvesData);

      } catch (err) {
        console.error("Falha no fetch:", err.message);
        setPokemons([]);
        setPokemonsType([]);
        setEvolves([]);
      } finally {
        setLoading(false);
      }
    }

    fetchData();
  }, []);

  if (loading || pokemons.length === 0 || pokemonstype.length === 0) {
    return <Spinner />;
  }

  return (
    <Card>
      {pokemons.map((pokemon) => {
        // procura se existe evolução correspondente
        const evoData = evolves.find(ev => ev.pokemon.number === pokemon.number);

        return (
          <PokemonCard key={pokemon.id}>
            {/* numero */}
            <PokeNumber>#{pokemon.id}</PokeNumber>

            {/* imagem do pokemon */}
            <PokemonImage
              src={pokemon.image || `http://localhost:8000/home/${pokemon.id}/img`}
              alt={pokemon.name}
            />

            {/* nome */}
            <PokeName style={{ textTransform: "uppercase" }}>{pokemon.name}</PokeName>

            {/* imagens dos tipos */}
            <PokemonImageTypeFrame>
              {(Array.isArray(pokemon.type) ? pokemon.type.slice(0, 2) : [pokemon.type])
                .filter(Boolean)
                .map((type) => (
                  <PokemonImageType
                    key={type}
                    src={`http://localhost:8000/type/${encodeURIComponent(type)}.png`}
                    alt={String(type)}
                  />
                ))}
            </PokemonImageTypeFrame>

            {/* evoluções */}
            {evoData && evoData.evolve.length > 0 ? (
              <EvoLine>
                {evoData.evolve.map((ev, idx) => (
                  <span key={ev.number} style={{ display: "flex", alignItems: "center", gap: 4 }}>
                    {idx > 0 && <Arrow>→</Arrow>}
                    <PokeImgEvo
                      src={`http://localhost:8000/home/${ev.number}/img`}
                      alt={ev.name}
                    />
                    <span style={{ fontSize: 12 }}>{ev.name}</span>
                  </span>
                ))}
              </EvoLine>
            ) : (
              <p style={{ fontSize: 12, color: "#777" }}>Não evolui</p>
            )}
          </PokemonCard>
        );
      })}
    </Card>
  );
}

export default CardPokemons;
