import styled from 'styled-components';

const InputCxBx = styled.input.attrs({ type: "checkbox" })`
  appearance: none;
  -webkit-appearance: none;
  -moz-appearance: none;

  width: 40px;
  height: 40px;
  border-radius: 50%;
  cursor: pointer;
  display: inline-block;
  position: relative;
  padding: 4px;

  /* fundo inicial e borda simulada */
  background: #ffffff4c;
  transition: background 2s ease, background-image 2s ease;

  &:checked {
    background: #000; /* fundo preto */
    background-image: linear-gradient(170deg, #f80606 50%, #fff 50%);
    background-origin: border-box;
    background-clip: content-box, border-box;
  }

  /* cria círculo interno para simular borda */
  &::before {
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    border-radius: 50%;
    box-sizing: border-box;
    padding: 4px; /* largura da “borda” */
    background: transparent;
    transition: background 2s ease;
  }
`;

export default InputCxBx;
