import { useEffect, useState } from "react";
import styled, { keyframes } from "styled-components";

const FrameDef = styled.div`
  display: flex;
  flex-direction: row;
  margin: 0 auto;
  margin-left: 10%;
  margin-right: 10%;
  background-image: linear-gradient(90deg,#002F52 35%,#326589 165%);
`;

const FramePokeImgType = styled.div`
  display: flex;
  flex-direction: column;
  gap: 15px;
  align-items: center;
  margin: 10px;
`;

const Card = styled.div`
  background: linear-gradient(-45deg, #e3f5fd, #c9e9fa, #e3f5fd);
  width: 100px;
  border-radius: 10px;
  text-align: center;
  padding: 5px;
`;

const PokeImgType = styled.img`
  width: 60px;
  height: 60px;
  object-fit: contain;
`;

const TitleType = styled.h3`
  text-transform: uppercase;
  font-size: 14px;
  margin: 5px 0;
`;

const spin = keyframes`
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
`;

const Spinner = styled.div`
  border: 6px solid #f3f3f3;
  border-top: 6px solid #3498db;
  border-radius: 50%;
  width: 60px;
  height: 60px;
  animation: ${spin} 1s linear infinite;
  margin: 50px auto;
`;

// Função para separar por descrição
function separarPorDescricao(data) {
  return {
    type: data.filter((item) => item.description === "type"),
    strong: data.filter((item) => item.description === "strong"),
    weak: data.filter((item) => item.description === "weak"),
  };
}

function FrameTypes() {
  const [tipos, setTipos] = useState({ type: [], strong: [], weak: [] });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    async function fetchTypes() {
      try {
        const res = await fetch("http://localhost:8000/typesFf");
        if (!res.ok) throw new Error("Erro ao buscar tipos");
        const data = await res.json();
        console.log("Resposta do backend /typesFf:", data);

        // separa os grupos
        setTipos(separarPorDescricao(data));
      } catch (err) {
        console.error("Erro no fetch:", err.message);
        setTipos({ type: [], strong: [], weak: [] });
      } finally {
        setLoading(false);
      }
    }

    fetchTypes();
  }, []);

  if (loading) return <Spinner />;
  if (!tipos.type.length && !tipos.strong.length && !tipos.weak.length)
    return <p>Nenhum tipo encontrado.</p>;

  return (
    <FrameDef>
      {/* Grupo TYPE */}
      <FramePokeImgType>
        <TitleType>TYPE</TitleType>
        {tipos.type.map((t) => (
          <Card key={t.name}>
            <TitleType>{t.name}</TitleType>
            <PokeImgType src={`http://localhost:8000${t.url}`} alt={t.name} />
          </Card>
        ))}
      </FramePokeImgType>

      {/* Grupo STRONG */}
      <FramePokeImgType>
        <TitleType>STRONG</TitleType>
        {tipos.strong.map((t) => (
          <Card key={t.name}>
            <TitleType>{t.name}</TitleType>
            <PokeImgType src={`http://localhost:8000${t.url}`} alt={t.name} />
          </Card>
        ))}
      </FramePokeImgType>

      {/* Grupo WEAK */}
      <FramePokeImgType>
        <TitleType>WEAK</TitleType>
        {tipos.weak.map((t) => (
          <Card key={t.name}>
            <TitleType>{t.name}</TitleType>
            <PokeImgType src={`http://localhost:8000${t.url}`} alt={t.name} />
          </Card>
        ))}
      </FramePokeImgType>
    </FrameDef>
  );
}

export default FrameTypes;
