import { useEffect, useState } from "react";
import styled from "styled-components";

const FramePokeEvoGrid = styled.div`
border: 2px solid #000;
  display: flex;
  justify-content: center;
  gap: 20px;
  flex-wrap: wrap;
  margin: 20px 0;
`;

const EvolveCard = styled.div`
border: 2px solid #000;

  display: flex;
  flex-direction: row;
  align-items: center;
  border-radius: 12px;
  padding: 12px;
  background: #f8f9fa;
  box-shadow: 0 4px 6px rgba(0,0,0,0.1);
`;

const PokeName = styled.p`
border: 2px solid #000;

  
  display: flex;
  flex-direction: column;
  // align-items: center;
  // justify-content: center;

  font-size: 10px;
  font-family: 'Montserrat','Helvetica Neue',Arial,sans-serif;
  font-weight: 300;
  line-height: 1;
  letter-spacing: 0.5px;
  color: #333;
`;

const EvoLine = styled.div`
border: 2px solid #000;

  display: flex;
  align-items: center;
  gap: 8px;
  margin-top: 8px;
`;

const PokeImgEvo = styled.img`
border: 2px solid #000;

  width: 100px;
  height: 100px;
  object-fit: contain;
`;

const Arrow = styled.span`
border: 2px solid #000;

  font-size: 18px;
  font-weight: bold;
`;

function FrameEvolves() {
  const [evolves, setEvolves] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    async function fetchEvolves() {
      try {
        const response = await fetch("http://localhost:8000/evolves");
        if (!response.ok) throw new Error("Erro ao buscar evoluções");
        const data = await response.json();
        // Filtra só os pokémons que têm evoluções
        setEvolves(data.filter(item => item.evolve && item.evolve.length > 0));
      } catch (err) {
        console.error("Falha no fetch /evolve:", err.message);
        setEvolves([]);
      } finally {
        setLoading(false);
      }
    }
    fetchEvolves();
  }, []);

  if (loading) {
    return <p>Carregando...</p>;
  }

  return (
    <FramePokeEvoGrid>
      {evolves.map((item) => (
        <EvolveCard key={item.id}>
          <PokeName>{item.pokemon.name}</PokeName>
          <PokeImgEvo
            src={`http://localhost:8000/home/${item.pokemon.number}/img`}
            alt={item.pokemon.name}
          />
          <EvoLine>
            {item.evolve.map((ev, idx) => (
              <span key={ev.number} style={{ display: "flex", alignItems: "center", gap: 4 }}>
                <Arrow>→</Arrow>
                <PokeName>{ev.name}</PokeName>
                <PokeImgEvo
                  src={`http://localhost:8000/home/${ev.number}/img`}
                  alt={ev.name}
                />
              </span>
            ))}
          </EvoLine>
        </EvolveCard>
      ))}
    </FramePokeEvoGrid>
  );
}
export default FrameEvolves;
