import { useEffect, useState } from 'react';
import styled, { keyframes } from 'styled-components';
import { get_fav, post_fav, del_fav } from '../services/fav';

const spin = keyframes`
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
`;
const FrameDef = styled.div`
  display: flex;
  flex-direction: column;
  gap: 20px;
  margin-left: 10%;
  margin-right: 10%;
  padding: 5px;
`;

const FrameRow = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  // gap: 10px;
  background-color: #ffffff3c;
  padding: 10px;
  border-radius: 20px;
  backdrop-filter: blur(10px);
  -webkit-backdrop-filter: blur(10px);
  box-shadow: 0 8px 20px rgba(0, 0, 0, 0.15);
`;

const Spinner = styled.div`
  border: 6px solid #f3f3f3;
  border-top: 6px solid #3498db;
  border-radius: 20px;
  width: 60px;
  height: 60px;
  animation: ${spin} 1s linear infinite;
  margin: 50px auto;
`;

function buildImgSrc(id) {
  return `http://localhost:8000/fav/${id}.png`;
}

/** Normaliza cada type em um bloco com { type, advantage[], weak[] } */
// function normalizeTypesFf(data) {
function normalizeData(data) {
  const result = [];

  data.forEach((obj) => {
    Object.entries(obj).forEach(([typeName, details]) => {
      result.push({
        type: { name: typeName, url: buildImgSrc(typeName) },
        advantage: (details.advantage || []).map((a) => ({
          name: a,
          url: buildImgSrc(a),
        })),
        weak: (details.foes || []).map((f) => ({
          name: f,
          url: buildImgSrc(f),
        })),
      });
    });
  });

  return result;
}

function FrameFavs() {
  const [blocks, setBlocks] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    async function fetchFavs() {
      try {
        // 1) Buscar lista de tipos (com imagens corretas)
        const resFavs = await fetch("http://localhost:8000/fav");
        if (!resFavs.ok) throw new Error("Erro ao buscar /favs");
        const favsData = await resFavs.json();

        // Monta dicionário 
        const favMap = {};
        favsData.forEach((t) => {
          favsData[t.name.toLowerCase()] = `http://localhost:8000${t.url}`;
        });

        // 2) Buscar relações advantage/foes
        const resFf = await fetch("http://localhost:8000/typesFf");
        if (!resFf.ok) throw new Error("Erro ao buscar /typesFf");
        const ffData = await resFf.json();

        // 3) Normalizar blocos
        const normalized = [];
        ffData.forEach((obj) => {
          Object.entries(obj).forEach(([typeName, details]) => {
            const typeKey = typeName.toLowerCase();

            // Só monta se o tipo tiver imagem no /types
            if (!favsData[typeKey]) return;

            normalized.push({
              type: { name: typeName, url: favsData[typeKey] },
              advantage: (details.advantage || []).map((a) => ({
                name: a,
                url: favsData[a.toLowerCase()] || "",
              })),
              weak: (details.foes || []).map((f) => ({
                name: f,
                url: favsData[f.toLowerCase()] || "",
              })),
            });
          });
        });

        console.log("✅ Normalizado com imagens:", normalized);
        setBlocks(normalized);
      } catch (err) {
        console.error("❌ Erro no fetch:", err);
        setBlocks([]);
      } finally {
        setLoading(false);
      }
    }

    fetchTypes();
  }, []);


  if (loading) return <Spinner />;

  return (
    <FrameDef>
    </FrameDef>
  );
}

export default FrameFavs;
